import React, { useState } from 'react';
import { Modal ,Button, List, ListItem, ListItemText } from '@material-ui/core';
import './Todo.css';
import db from "./firebase"
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function Todo(props) {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [input, setInput] = useState();

  const handleOpen = () => {
    setOpen(true);
  };

  const updateTodo = () => {
    db.collection('todos').doc(props.todo.id).set({
      todo: input
    }, { merge: true });

    setOpen(false);
  }


  return (
    <>

  <Modal
    open={open}
    onClose={e => setOpen(false)}
    aria-labelledby="simple-modal-title"
    aria-describedby="simple-modal-description"
>
  <div className={classes.paper}>
    <h4>üìù Update your todo</h4>
    <p> {props.todo.todo}</p>
    <input placeholder="Type in new value here..."value={input} onChange={event => setInput(event.target.value)}/>
    <Button onClick={updateTodo}>Update Todo</Button>
  </div>
  </Modal>
    <List className="todo__list">
      <ListItem>
        <ListItemText primary={props.todo.todo} secondary="‚è∞ Deadline: ASAP"/>
      </ListItem>
      <Button onClick={e => setOpen(true)}>Update Todo</Button>
      <DeleteIcon onClick={event =>
       db.collection('todos').doc(props.todo.id).delete()
     }/>
    </List>
    </>
  )
}

export default Todo
